import logo from './logo.svg';
import './App.css';
import {useState} from 'react'; 
import {Routes, Route, Link} from 'react-router-dom';
import data from './data';
import MainShowCase from './component/MainShowCase';
import Detail from './component/Detail';
import Footer from './component/Footer';


function App() {

  let [pang, setPang] = useState(data)
  {/* 찌찌 */ }
  return (
    <div>
      <div className="navContainer">
        <div className='innerNav'>
          <Link className="navMenu" to="/"><p>놀부네 빵앗간</p></Link>
          <ul>
            <li onClick={()=>{ console.log('빵/샌드위치')}}>빵/샌드위치</li> { /*빵을 누르면 pand.sort.bread종류만 보여줘 */ }
            <li>케이크</li>
            <li>디저트</li>
            <li>샐러드</li>
          </ul>
          <img className="hamburger" src={process.env.PUBLIC_URL + "/menu.png"}/>
        </div>
      </div>
      <div className="main-bg"></div>

      <Routes>
        <Route path="/" element={<div><MainShowCase pang={pang} /></div>} />
        <Route path="/detail/:id" element={<div><Detail pang={pang[0]}/></div>} />
      </Routes>

      <Footer />
    </div>
  );
}

export default App;
