import logo from './logo.svg';
import './App.css';
import {useState} from 'react'; 
import {Routes, Route, Link, useNavigate} from 'react-router-dom';
import data from './data';
import data2 from './data2';
import data3 from './data3';
import data4 from './data4';
import chiefdata from './chiefdata';
import MainShowCase from './component/MainShowCase';
import Detail from './component/Detail';
import Cart from './component/Cart';
import Footer from './component/Footer';



function App() {
  let [pang, setPang] = useState(data);
  let [pang2,setPang2] = useState(data2);
  let [pang3,setPann3] = useState(data3);
  let [pang4,setPang4] = useState(data4);
  let [chief,setChief] = useState(chiefdata);
  let navigate = useNavigate();

  let [count, setCount] = useState(0); //실험 변수

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
  })}

  return (
  <div>
    <div className='quick'>
        <img className='sign' src={process.env.PUBLIC_URL + '/call.png'} />
      </div>
      <div className='quick2' onClick={()=>{ 
          scrollToTop();
          navigate("/cart")
        }}>
        <img className='sign2' src={process.env.PUBLIC_URL + '/shopping-bag.png'} />
      </div>
      <div className="navContainer">
        <div className='innerNav'>
          <p className="home" onClick={()=>{navigate("/")}}>Un Bouquet De Lavande</p>
          <ul>
            <li>로그인</li>
            <li>회원가입</li>
            <Link className='menuList' to="/cart"><li>장바구니</li></Link>
          </ul>
          <img className="hamburger" src={process.env.PUBLIC_URL + "/menu.png"}/>
        </div>
      </div>
      <div className="main-bg"></div>


      {/* 실험 */}


      <div className='MainShowCase'>
      <div className='pangMenu'>
        <ul>
          <li>전체상품</li>
          <li>빵/샌드위치</li>
          <li>케이크</li>
          <li>디저트</li>
          <li>샐러드</li>
        </ul>
      </div>
      <div className="sort">
        <button className="expensiveBtn">높은가격순</button>
        <button className="chipBtn">낮은가격순</button>
        <button className="abcBtn"
          onClick={()=>{
          let copy =[...pang, ...pang2, ...pang3, ...pang4]

          copy.sort(
            (a, b)=>{
            if(a.title.toLowercase() > b.title.toLowercase()) return 1
            else if(a.title.toLowercase() < b.title.toLowercase()) return -1
            else return 0
          })
         }}
        >가나다순</button>
      </div>
      <div className="cardContainer">
        {
          pang.map((a, i) => {
          return(
            <Link to={`/detail/${i}`} key={i}>
              <Card pang={pang[i]} ></Card>
            </Link>
           );
          })
        }
      </div>
      <div className='moreBtnContainer'>
        <button className='moreBtn' onClick={()=>{
          setCount(++count);
          if(count == 1){
            let copy = [...pang, ...pang2];
            setPang(copy);
          }
          else if(count == 2){
            let copy = [...pang, ...pang3];
            setPang(copy);
          }
          else if(count == 3){
            let copy =[...pang, ...pang4];
            setPang(copy);
          }
          else{
            alert('더이상 상품이 없습니다.');
          }
        }}>더보기</button>
      </div>
    </div>

      <Routes>
        <Route path="/" element={<div><MainShowCase pang={pang} pang2={pang2} pang3={pang3} pang4={pang4} setPang={setPang} /></div>} />
        <Route path="/detail/:id" element={<div><Detail pang={pang} pang2={pang2} pang3={pang3} pang4={pang4} setPang={setPang} chief={chief} /></div>} />
        <Route path="/cart" element={<div><Cart/></div>} />
        <Route path="*" element={<div>해당 페이지를 찾을 수 없습니다.</div>} />
      </Routes>

      <Footer />
  </div>
  );
}

function Card({pang}){
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    })
  };
  return (
    <div className="eachCard Product" onClick={()=>{scrollToTop();}}>
      <img src={process.env.PUBLIC_URL + pang.image} />
      <p>
      {pang.title}
      <br/>
      {pang.price}
      </p>
    </div>
  )
};


export default App;
