import React from 'react';
import Slider from "react-slick";

function SimpleSlider(){

  const settings ={
    dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
  }

  return (
    <>
      <div className="train">
        <h2>신상품이 왔어요.</h2>
        <Slider className="trainContainer" {...settings}>
          <div className="innerTrain">
            <img className="innerPhone" src={process.env.PUBLIC_URL + "/rolloutPhone.png"}/>
          </div>
          <div className="innerTrain">

            <img
              className="innerPhone"
              src={process.env.PUBLIC_URL + "/rolloutPhone2.png"}
            />
          </div>
          <div className="innerTrain">

            <img
              className="innerPhone"
              src={process.env.PUBLIC_URL + "/rolloutPhone3.png"}
            />
          </div>
          <div className="innerTrain">

            <img
              className="innerPhone"
              src={process.env.PUBLIC_URL + "/rolloutPhone4.png"}
            />
          </div>
        </Slider>
      </div>
      <img className="leftArrow" src={process.env.PUBLIC_URL + "/back.png"} />
      <img className="rightArrow" src={process.env.PUBLIC_URL + "/right.png"} />
    </>
  );
};

export default SimpleSlider;
