import logo from './logo.svg';
import './App.css';
import {useState} from 'react'; 
import {Routes, Route, Link} from 'react-router-dom'
import data from './data';
import MainShowCase from './component/MainShowCase';
import Footer from './component/Footer';

function App() {

  let [pang, setPang] = useState(data)

  return (
    <div>
      <div className="navContainer">
        <div className='innerNav'>
          <p><a className="home" href="#">놀부네 빵앗간</a></p>
          <ul>
            <li><Link to="/bread">빵/샌드위치</Link></li>
            <li><Link to="/cake">케이크</Link></li>
            <li><Link to="desert">디저트</Link></li>
            <li><Link to="">샐러드</Link></li>
          </ul>
          <img className="hamburger" src={process.env.PUBLIC_URL + "/menu.png"}/>
        </div>
      </div>
      <div className="main-bg"></div>

      <Routes>
        <Route path="/" element={<div><MainShowCase pang={pang} /></div>} />
        <Route path="/detail" element={<div>상세페이지임</div>} />
      </Routes>

      <Footer />
    </div>
  );
}

export default App;
