import data from "../data";
import React, {useState} from 'react';

function Slider({dataSlider}){
  return(
    <div className="sliderContainer">
      <h2>진행중인 이벤트</h2>
      {
        dataSlider.map((a, i)=>{
          return(
            <div className="slide">
              <img src={ process.env.PUBLIC_URL + `/rollout${i + 1}.png` }/>
            </div>
          )
        })
      }
    </div>
  )
};
export default Slider;
