import logo from './logo.svg';
import './App.css';
import { useState, useEffect, useCallback } from 'react'; 
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import data from './data';
import data2 from './data2';
import data3 from './data3';
import data4 from './data4';
import chiefdata from './chiefdata';
import Detail from './component/Detail';
import Cart from './component/Cart';
import Footer from './component/Footer';
import Quick from './component/Quick';



function App() {
  useEffect(()=>{
    if(localStorage.getItem('watched') == null){
      localStorage.setItem('watched', JSON.stringify([ ]))
    }
  },[]);

  let [pang, setPang] = useState(data);
  let [pang2,setPang2] = useState(data2);
  let [pang3,setPann3] = useState(data3);
  let [pang4,setPang4] = useState(data4);
  let [chief,setChief] = useState(chiefdata);
  let [count, setCount] = useState(0);
  let navigate = useNavigate();


  return (
    <div>
      <div className="navContainer">
        <div className="innerNav">
          <p className="home" onClick={() => { navigate("/")}}>
            Un Bouquet De Lavande
          </p>
          <ul>
            <li>로그인</li>
            <Link className="menuList" to="/cart"><li>장바구니</li></Link>
          </ul>
          <div className="iconArea">
            <img className="logIn" src={process.env.PUBLIC_URL + "/avatar.png"}/>
            <Link to="/cart"><img className="cartIcon" src={process.env.PUBLIC_URL + "/cart.png"}/></Link>
          </div>
        </div>
      </div>
      <div className="main-bg"></div>
      <Routes>
        <Route path="/" element={
            <div className="MainShowCase">
              <div className="pangMenu">
                <ul>
                  <li eventkey="link0" onClick={() => {  }}>전체상품</li>
                  <li eventkey="link1" onClick={() => {  }}>빵/샌드위치</li>
                  <li eventkey="link2" onClick={() => {  }}>케이크</li>
                  <li eventkey="link3" onClick={() => {  }}>디저트</li>
                  <li eventkey="link4" onClick={() => {  }}>샐러드</li>
                </ul>
              </div>
              {
                tab == 0
              }
              <div className="cardContainer">
                {pang.map((data, i) => {
                  return (
                    <Link to={`/detail/${i}`} key={i}>
                      <Card pang={pang[i]}></Card>
                    </Link>
                  );
                })}
              </div>
              <div className="moreBtnContainer">
                <button
                  className="moreBtn"
                  onClick={() => {
                    setCount(++count);
                    if (count == 1) {
                      let copy = [...pang, ...pang2];
                      setPang(copy);
                    } else if (count == 2) {
                      let copy = [...pang, ...pang3];
                      setPang(copy);
                    } else if (count == 3) {
                      let copy = [...pang, ...pang4];
                      setPang(copy);
                    } else {
                      alert("더이상 상품이 없습니다.");
                    }
                  }}
                >더보기</button>
              </div>

              <div className="event">
                <h2>진행중인 이벤트</h2>
                <img src={process.env.PUBLIC_URL + "/event.png"} />
              </div>
              <div className="announcement">
                <h2>공지사항</h2>
                <ul>
                  <li>
                    <p>네이버페이 제휴 포인트 적립서비스 안내</p>
                    <span>2022–09-25</span>
                  </li>
                  <li>
                    <p>KTX특송 배송제한으로 인한 배송불가지역 안내</p>
                    <span>2022–09-02</span>
                  </li>
