      state.push(action.payload)
    },
    addCount(state, action){
      let idSyncIndex = state.findIndex((state)=>{return state.id === action.payload })
      state[idSyncIndex].count += 1
    },
    subtractCount(state, action){
      let idSyncIndex = state.findIndex((state)=>{return state.id === action.payload })
      state[idSyncIndex].count -= 1
      state[idSyncIndex]
