import './App.css';
import { useState, useEffect, useRef } from 'react'; 
import { Routes, Route, Link, useNavigate } from 'react-router-dom';

import data from './data';
import chiefdata from './chiefdata';

import Nav from './component/Nav';
import Card from './component/Card';
import Slider from './component/Slider';
import Detail from './component/Detail';
import Cart from './component/Cart';
import Parallels from './component/Parallels';
import Announce from './component/Announce';
import Footer from './component/Footer';
import Quick from './component/Quick';


function App() {

  //sessionStorage
  let storage = JSON.parse(sessionStorage.getItem('watched'))
  useEffect(()=>{
    //sessionStorage
    if(storage == null){
      sessionStorage.setItem('watched', JSON.stringify([ ]))
    }
  },[]);

  //페이지전환시 스크롤초기화
  const scrollToTop = ()=>{
    window.scrollTo({
    top: 0,
    behavior: 'smooth'
  })}

  //데이터들
  let [pang, setPang] = useState(data);
  // let [dataSlider, setDataSlider] = useState(data2);
  let [chief,setChief] = useState(chiefdata);

  let [count, setCount] = useState(0);
  let [more, setMore] = useState(12);
  let [tab,setTab] = useState(0);
  let [moreNum, setMoreNum] = useState(1);




  //하단 텍스트 애니메이션
  const textArr1 = 'PARIS BAGUETTE - PARIS BAGUETTE -'.split(' ');
  const textArr2 = '- PARIS BAGUETTE '.split(' ');
  let count1 = 0;
  let count2 = 0;

  useEffect(()=>{
    function initTexts(pTag, textArray){
      textArray.push(...textArray)
      for(let i = 0; i < textArray.length; i++){
        pTag.current.innerText += `${textArray[i]}\u00a0\u00a0\u00a0`
      }
    }
    initTexts(pTag1,textArr1);
    initTexts(pTag2,textArr2);

    function marqueeText(count,pTag,direction){
      if( count > pTag.scrollWidth / 2 ){
        pTag.current.style.transform = `translateX(0)`
        count = 0
      }
      pTag.current.style.transform = `translateX(${count * direction}px)`
      return count;
    }

    function animate(){
      count1++
      count2++

      count1 = marqueeText(count1,pTag1,-1) //왼쪽으로
      count2 = marqueeText(count2,pTag2, 1) //오른쪽으로

      window.requestAnimationFrame(animate)
    }
    animate();
  }) //무한반복을 위한 []부재처리


  return (
    <div>
      <Nav/>

      <Routes>
        <Route path="/" element={
          <div>
            <Slider/>
            <div className="MainShowCase">
              <div className="pangMenu">
                <h2>OUR PRODUCTS</h2>
                <p>상세페이지에서 제품별 영양성분/알레르기 정보를 확인하실 수 있습니다.</p>
                <ul>
                  <li onClick={() => { setTab(0) }}>ALL</li>
                  <li onClick={() => { setTab(1) }}>BREAD</li>
                  <li onClick={() => { setTab(2) }}>CAKE</li>
                  <li onClick={() => { setTab(3) }}>DESERT</li>
                  <li onClick={() => { setTab(4) }}>SALAD</li>
                </ul>
              </div>
              {
                tab == 0 ? //전체상품
                <div>
                  <div className="cardContainer">
                    {
                      pang.map((data, i) => {
                        if(i < more){
                        return (
                          <Link to={`/detail/${i}`} key={i}>
                            <Card pang={pang[i]} scrollToTop={scrollToTop}></Card>
                          </Link>
                        )
                        }
                    })}
                  </div>
                  <div className="moreBtnContainer">
                    <button
                      className="moreBtn"
                      onClick={() => { {/* 더보기버튼 */}
                        setCount(++count);
                        if (count == 1) {
                          setMore(24)
                          setMoreNum(2)
                        } else if (count == 2) {
                          setMore(36)
                          setMoreNum(3)
                        } else if (count == 3) {
                          setMore(40)
                          setMoreNum(4)
                        } else {
                          alert("더이상 상품이 없습니다.");
                        }
                      }}
                    >MORE {moreNum} / 4</button>
                  </div>
                </div>
                : null
              }
              {
                tab == 1 ? //빵 샌드위치
                <div>
                  <div className="cardContainer">
                    {pang.map((data, i) => {
                      if(data.sort == 'bread'){
                        return (
                          <Link to={`/detail/${i}`} key={i}>
                            <Card pang={pang[i]} scrollToTop={scrollToTop}></Card>
                          </Link>
                        )
                      }
                    })}
                  </div>
                </div>
                : null
              }
              {
                tab == 2 ? //케이크
                <div>
                  <div className="cardContainer">
                    {pang.map((data, i) => {
                      if(data.sort == 'cake'){
                        return (
                          <Link to={`/detail/${i}`} key={i}>
                            <Card pang={pang[i]} scrollToTop={scrollToTop}></Card>
                          </Link>
                        )
                      }
                    })}
                  </div>
                </div>
                : null
              }
              {
                tab == 3 ? //디저트
                <div>
                  <div className="cardContainer">
                    {pang.map((data, i) => {
                      if(data.sort == 'desert'){
                        return (
                          <Link to={`/detail/${i}`} key={i}>
                            <Card pang={pang[i]} scrollToTop={scrollToTop}></Card>
                          </Link>
                        )
                      }
                    })}
                  </div>
                </div>
                : null
              }
              {
                tab == 4 ? //샐러드
                <div>
                  <div className="cardContainer">
                    {pang.map((data, i) => {
                      if(data.sort == 'salad'){
                        return (
                          <Link to={`/detail/${i}`} key={i}>
                            <Card pang={pang[i]} scrollToTop={scrollToTop}></Card>
                          </Link>
                        )
                      }
                    })}
                  </div>
                </div>
                : null
              }
              <div className='recentView'>
