import react, {memo, useState}  from 'react';
import {Link} from 'react-router-dom';

function Showcase({ }){
  return (
    <div>
      <div className="cardContainer">
        {pang.map((data, i) => {
          return (
            <Link to={`/detail/${i}`} key={i}>
              <Card pang={pang[i]}></Card>
            </Link>
          );
        })}
      </div>
      <div className="moreBtnContainer">
        <button
          className="moreBtn"
          onClick={() => {
            setCount(++count);
            if (count == 1) {
              let copy = [...pang, ...pang2];
              setPang(copy);
            } else if (count == 2) {
              let copy = [...pang, ...pang3];
              setPang(copy);
            } else if (count == 3) {
              let copy = [...pang, ...pang4];
              setPang(copy);
            } else {
              alert("더이상 상품이 없습니다.");
            }
          }}
        >더보기</button>
      </div>
    </div>
  );
};

function Card({pang}){
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    })
  };
  return (
    <div className="eachCard Product" onClick={()=>{scrollToTop();}}>
      <img src={process.env.PUBLIC_URL + pang.image} />
      <p className='minidesc'>{pang.title}</p>
      <p className='minidesc'>{pang.price.toLocaleString('ko-KR')}원</p>
    </div>
  )
};



export default Showcase;
