import logo from './logo.svg';
import './App.css';
import { useState, useEffect, useCallback } from 'react'; 
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import data from './data';
import data2 from './data2';
import data3 from './data3';
import data4 from './data4';
import chiefdata from './chiefdata';
import MainShowCase from './component/MainShowCase';
import Detail from './component/Detail';
import Cart from './component/Cart';
import Footer from './component/Footer';



function App() {
  useEffect(()=>{
    if(localStorage.getItem('watched') == null){
      localStorage.setItem('watched', JSON.stringify([ ]))
    }
  },[]);

  let [pang, setPang] = useState(data);
  let [pang2,setPang2] = useState(data2);
  let [pang3,setPann3] = useState(data3);
  let [pang4,setPang4] = useState(data4);
  let [chief,setChief] = useState(chiefdata);
  let navigate = useNavigate();


  let [count, setCount] = useState(0);
  let [pangSort, setPangSort] = useState('');


  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
  })}


  return (
    <div>
      <div className="navContainer">
        <div className="innerNav">
          <p
            className="home"
            onClick={() => {
              navigate("/");
            }}
          >
            Un Bouquet De Lavande
          </p>
          <ul>
            <li>로그인</li>
            <Link className="menuList" to="/cart">
              <li>장바구니</li>
            </Link>
          </ul>
          <div className="iconArea">
            <img
              className="logIn"
              src={process.env.PUBLIC_URL + "/avatar.png"}
            />
            <Link to="/cart">
              <img
                className="cartIcon"
                src={process.env.PUBLIC_URL + "/cart.png"}
              />
            </Link>
          </div>
        </div>
      </div>
      <div className="main-bg"></div>
      <Routes>
        <Route
          path="/"
          element={
            <div className="MainShowCase">
              <div className="pangMenu">
                <ul>
                  <li
                    onClick={() => {
                      setPangSort();
                    }}
                  >
                    전체상품
                  </li>
                  <li
                    onClick={() => {
                      setPangSort("bread");
                    }}
                  >
                    빵/샌드위치
                  </li>
                  <li
                    onClick={() => {
                      setPangSort("cake");
                    }}
                  >
                    케이크
                  </li>
                  <li
                    onClick={() => {
                      setPangSort("desert");
                    }}
                  >
                    디저트
