import logo from './logo.svg';
import './App.css';
import {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, Navbar, Nav} from 'react-bootstrap';
import data from './data';

function App() {

  let [pang, setPang] = useState(data)

  return (
    <div className='App'>
      <Navbar bg="light" variant="light">
        <Container className='nav'>
          <Navbar.Brand>놀부네 빵앗간</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link>베스트</Nav.Link>
            <Nav.Link>케이크</Nav.Link>
            <Nav.Link>샌드위치</Nav.Link>
            <Nav.Link>디저트</Nav.Link>
            <Nav.Link>샐러드</Nav.Link>
          </Nav>
        </Container>
      </Navbar>
      <div className='main-bg'></div>
      <div className='sort'>
        <button className='bestBtn'>인기순</button>
        <button className='abcBtn'>가나다순</button>
      </div>
      <div className='cardContainer'>
      {
        pang.map((a,i)=>{
          return(
            <Card pang={pang[i]}></Card>
          )
        })
      }
      </div>
      <footer>
        <h5>INFO</h5>
        <p>브랜드소개</p>
        <p>창업안내</p>
        <p>채용</p>
      </footer>
    </div>
  );
}
function Card({pang}){
  return (
    <div className="showcase">
      <img src={process.env.PUBLIC_URL + pang.image} />
      <p>
      {pang.title}
      <br/>
      {pang.price}
      </p>
    </div>
  )
};

export default App;
