import logo from './logo.svg';
import './App.css';
import {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, Navbar, Nav} from 'react-bootstrap';
import data from './data';

function App() {

  let [pang, setPang] = useState(data)

  return (
    <div className='App'>
      <Navbar bg="light" variant="light">
        <Container>
          <Navbar.Brand>Le pain Quotidien</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link>베스트</Nav.Link>
            <Nav.Link>케이크</Nav.Link>
            <Nav.Link>샌드위치</Nav.Link>
            <Nav.Link>디저트</Nav.Link>
            <Nav.Link>샐러드</Nav.Link>
          </Nav>
        </Container>
      </Navbar>
      <div className='main-bg'></div>
      <div className='sort'>
        <button className='bestBtn'>인기순</button>
        <button className='abcBtn'>가나다순</button>
      </div>
      <div className='cardContainer'>

        <Card pang={pang[0]}/>
        <Card pang={pang[0]}/>
        <Card pang={pang[0]}/>
        <Card pang={pang[0]}/>

      </div>
    </div>
  );
}
function Card({pang}){
  return (
    <div className="showcase">
      <img src={process.env.PUBLIC_URL + "./greencake.png"} />
      <p>{pang.title}</p>
      <p>{pang.price}</p>
    </div>
  )
};

export default App;
