import react, {memo, useState}  from 'react';
import {Link} from 'react-router-dom';

function MainShowCase({pang, pang2, pang3, pang4, setPang}){

  let [count, setCount] = useState(0);
  let [pangSort, setPangSort] = useState('');
  return (
    <div className='MainShowCase'>
      <div className='pangMenu'>
        <ul>
          <li onClick={()=>{
            setPangSort()
          }}>전체상품</li>
          <li onClick={()=>{
            setPangSort('bread')
          }}>빵/샌드위치</li>
          <li onClick={()=>{
            setPangSort('cake')
          }}>케이크</li>
          <li onClick={()=>{
            setPangSort('desert')
          }}>디저트</li>
          <li onClick={()=>{
            setPangSort('salad')
          }}>샐러드</li>
        </ul>
      </div>
      <div className="cardContainer">
        {
          pang.map((data, i) => {
                return (
                  <Link to={`/detail/${i}`} key={i}>
                    <Card pang={pang[i]} ></Card>
                  </Link>
                );
          })
        }
      </div>
      <div className='moreBtnContainer'>
        <button className='moreBtn' onClick={()=>{
          setCount(++count);
          if(count == 1){//사이트에서 오류가 나는 원인이 아래의 코딩인 것으로 추정...수정해야할 것 같음. ㅠㅠ
            let copy = [...pang, ...pang2];
            setPang(copy);
          }
          else if(count == 2){
            let copy = [...pang, ...pang3];
            setPang(copy);
          }
          else if(count == 3){
            let copy =[...pang, ...pang4];
            setPang(copy);
          }
          else{
            alert('더이상 상품이 없습니다.');
          }
        }}>더보기</button>
      </div>
      <div className='event'>
        <h2>진행중인 이벤트</h2>
        <img src={process.env.PUBLIC_URL + "/event.png"}/>
      </div>
      <div className="announcement">
        <h2>공지사항</h2>
        <ul>
          <li><p>네이버페이 제휴 포인트 적립서비스 안내</p> <span>2022–09-25</span></li>
          <li><p>KTX특송 배송제한으로 인한 배송불가지역 안내</p> <span>2022–09-02</span></li>
          <li><p>코레일 수화물센터 파업으로 인한 긴급안내</p> <span>2022-09-23</span></li>
          <li><p>순우유케이크 가격 인상안내</p> <span>2022-08-28</span></li>
          <li><p>상반기 배송 이벤트 당첨자 발표</p> <span>2022-08-25</span></li>
          <li><p>용인 수화물센터 운영종료 및 갱신</p> <span>2022-07-14</span></li>
          <li><p>KTX특송 이용 및 수화물센터 운영종료 및 갱신</p> <span>2022-06-10</span></li>
          <li><p>배송시간 변경안내 및 추가 배송불가지역 안내</p> <span>2022-06-08</span></li>
        </ul>
      </div>
    </div>
  );
};

function Card({pang}){
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    })
  };
  return (
    <div className="eachCard Product" onClick={()=>{scrollToTop();}}>
      <img src={process.env.PUBLIC_URL + pang.image} />
      <p>
      {pang.title}
      <br/>
      {pang.price.toLocaleString('ko-KR')}원
      </p>
    </div>
  )
};


export default MainShowCase;
