import react from 'react';
import { useSelector } from 'react-redux';

function Cart(){


  let state = useSelector((state)=>{return state})
  console.log(state);

  return (
    <div className="Cart">
      <table>
        <thead>
          <tr>
            <th>상품</th>
            <th>수량</th>
            <th>변경</th>
            <th>가격</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
        {
          state.cart.map((a, i)=>{
            return <tr key={i}>
            <td>{state.cart[i].title}</td>
            <td>{state.cart[i].count}개</td>
            <td>
              <button className='countUp'> ↑ </button>
              <button className='countDown'> ↓ </button>
            </td>
            <td>{state.cart[i].price}</td>
            <td>삭제</td>
          </tr>
          })
        }
          {/* <tr>
            <td>{state.cart[0].title}</td>
            <td>{state.cart[0].count}개</td>
            <td>쁠/마/삭제</td>
            <td>{state.cart[0].price}</td>
          </tr> */}
        </tbody>
      </table>
      <div className="orderArea">
        <button className="orderBtn2">주문하기</button>
      </div>
    </div>
  );
};
export default Cart;
